    public static string GeneratePistolName(Rarity rarity)
    {
        switch (rarity)
        {
            case Rarity.Common:
                return pistolCommonAdjectives[Random.Range(0, pistolCommonAdjectives.Count - 1)] + " " + pistolNouns[Random.Range(0, pistolNouns.Count - 1)];
            case Rarity.Uncommon:
                return pistolAdjectives[Random.Range(0, pistolAdjectives.Count - 1)] + " " + pistolNouns[Random.Range(0, pistolNouns.Count - 1)];
            case Rarity.Rare:
                return rarePistolNames[Random.Range(0, rarePistolNames.Count - 1)];
            case Rarity.UltraRare:
            case Rarity.God:
                return rarePistolNames[Random.Range(0, rarePistolNames.Count - 1)] + " " + coolPistolNameStuff[Random.Range(0, coolPistolNameStuff.Count - 1)] + " " + ofEnding[Random.Range(0, ofEnding.Count - 1)];
        }
        return "Blank Weapon";
    }

